section .text
    extern srand
    extern rand
    extern printf
    extern get_rand
    
    global main
    

;; ========================================================================== ;;
;; void decrypt()
;; decripteaza linkurile secrete
decrypt:
    enter 0, 0

    push esi                ; save registers
    push edi                ;
    
    push textsize           ;
    call srand              ; srand(textsize);
    add esp, 4              ;

    mov edi, all_links_size ; n = all_links_size;
    xor esi, esi            ; i = 0;

decrypt_char:               ; do {
    call rand               ;   x = rand();
    push eax                ;   push x for srand

    xor al, [links + esi]   ; 
    mov [links + esi], al   ;   link[i] ^= x;

    call srand              ;   srand(x);
    add esp, 4              ;

    inc esi                 ;  i++;
    cmp esi, edi            ;
    jl decrypt_char         ; } while (i < n);

    pop edi                 ; restore registers
    pop esi                 ;

    leave
    ret


;; ========================================================================== ;;
;; int main(void)
main:
    enter 0, 0

    push ebx                        ; save registers
    push esi                        ;       
    push edi                        ;

    call get_rand                   ; i = get_rand();
    test eax, eax                   ; if (i == -0)
    jz gdb_detected                 ;   return -1;
    
    xor edx, edx                    ;   
    mov ebx, link_num               ;
    div ebx                         ; i = rand() % link_num;       
    imul edx, edx, link_size        ; 

    lea esi, [links + edx]          ; link = links[i];

    call decrypt                    ; decrypt();

    mov byte [esi + link_size], 0x0 ; link[link_size] = '\0';

    push esi                        ;
    push fmt                        ;
    call printf                     ; printf(fmt, link);
    add esp, 8                      ;

    xor eax, eax                    ; return 0;
    leave
    ret

gdb_detected:
    xor eax, eax                    ;
    not eax                         ; return -1;
    leave
    ret

    textsize equ $ - $$             ;; dimensiunea sectiunii .text

